#!/bin/sh
rm -rf "'"$TMPDIR"'"
EOF
chmod +x /tmp/sudo; echo "Created wrapper /tmp/sudo (executes rm -rf on the test dir)."; PATH="/tmp:$PATH"; echo "Invoking sudo (should run /tmp/sudo)..."; sudo || true; echo "After:"; ls -la "$TMPDIR" || echo "Test dir removed"; rm -f /tmp/sudo'"}

